import java.util.*;
import java.util.Arrays;

public class Array {

	
	static void vector(){
		
		
		List<String> lista = Arrays.asList("lunes", "martes", "miercoles", "jueves");
		
		for(String dia : lista){
			
			System.out.print("El dia es : " + dia);
		}
		
		
		
	}
	
	
	
	
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		vector();
		
		
	}

}
